    private UserSysmumModel user;
    public string LoginMesssage { get; set; }

    protected override Task OnInitializedAsync()
    {
        user = new UserSysmumModel();
        return base.OnInitializedAsync();
    }

    private async Task<bool> ValidateUser()
    {
        List<UserSysmumModel> newuser = await _db.getUser(user);
        if (newuser.Count == 0)
        {
            LoginMesssage = "User doesnt exist";
        }
        else
        {
            String password = newuser[0].password;
            if (user.password == password)
            {
                LoginMesssage = "Success! Logging in...";
                ((Auth)AuthenticationStateProvider).MarkUserAsAuthenticated(user.username);
                await sessionStorage.SetItemAsync("username", user.username);
                await JSRuntime.InvokeVoidAsync("close");
            }
            else
            {
                LoginMesssage = "Wrong Password!";
            }
        }

        await JSRuntime.InvokeVoidAsync("popup2");


        return await Task.FromResult(true);
    }


